import "../style/App.scss";
import React from "react";

class Ejercicio1 extends React.Component {
  constructor(props) {
    super(props);
  }

  handleInput(ev) {
    this.setState({
      input: ev.target.value,
    });
  }

  render() {
    return (
      <div>
        <form>
          <input type='text' id='input' onChange={this.handleInput}></input>
        </form>
        <p></p>
      </div>
    );
  }
}

export default Ejercicio1;
